import React, { useEffect, useState } from "react";
import { RetellWebClient } from "retell-client-js-sdk";
import { Telephone, TelephoneX } from 'react-bootstrap-icons';
import './App.css';

const agentId = "agent_f7fb4bf17b19155224cbc6f4f1";
const retellWebClient = new RetellWebClient();

const App = () => {
  const [isCalling, setIsCalling] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [hasAgentStarted, setHasAgentStarted] = useState(false);

  useEffect(() => {
    retellWebClient.on("agent_start_talking", () => {
      console.log("Agent is talking...");
      setIsListening(false); // You should stop talking
    });

    retellWebClient.on("agent_stop_talking", () => {
      console.log("Agent stopped talking.");
    });

    retellWebClient.on("transcript", (data) => {
      console.log("Transcript:", data);
    });

    const handleKeyDown = async (e) => {
      if (e.code === "Space" && isCalling && !isListening) {
        e.preventDefault();
        console.log("Spacebar pressed → YOU talk");
        // (Optional) start recording logic here
        setIsListening(true);
      }
    };

    const handleKeyUp = async (e) => {
      if (e.code === "Space" && isCalling && isListening) {
        e.preventDefault();
        console.log("Spacebar released → YOU stop talking");
        // (Optional) stop recording logic here
        setIsListening(false);
      }
    };

    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);

    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, [isCalling, isListening]);

  const startCall = async () => {
    console.log("Starting call...");
    const registerCallResponse = await registerCall(agentId);

    if (registerCallResponse.call_id && registerCallResponse.websocket_url) {
      await retellWebClient.startConversation({
        callId: registerCallResponse.call_id,
        websocketUrl: registerCallResponse.websocket_url,
      });
      setIsCalling(true);
      setHasAgentStarted(true);
      console.log("Conversation started!");
    } else {
      console.error("Invalid response:", registerCallResponse);
    }
  };

  const stopCall = async () => {
    console.log("Hanging up...");
    await retellWebClient.hangUp();
    setIsCalling(false);
    setHasAgentStarted(false);
  };

  const registerCall = async (agentId) => {
    try {
      const response = await fetch("https://al-wathba-backend.onrender.com/create-web-call", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ agent_id: agentId }),
      });
      return await response.json();
    } catch (error) {
      console.error("Error registering call:", error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h2>AL Wathba Demo - Hindi</h2>

        <button
          onClick={isCalling ? stopCall : startCall}
          className={`call-button ${isCalling ? "stop" : "start"}`}
        >
          {isCalling ? <TelephoneX size={40} /> : <Telephone size={40} />}
        </button>

        {isCalling && (
          <p style={{ marginTop: '20px' }}>
            Press & Hold <strong>Spacebar</strong> to Talk. Release to Listen!
          </p>
        )}
      </header>
    </div>
  );
};

export default App;
