import React, { useEffect, useState, useRef } from "react";
import "./App.css";
import { RetellWebClient } from "retell-client-js-sdk";
import { Telephone, TelephoneX, Mic, MicMute } from 'react-bootstrap-icons';
import '@fontsource/raleway';

const agentId = "agent_f7fb4bf17b19155224cbc6f4f1";
const retellWebClient = new RetellWebClient();

const App = () => {
  const [isCalling, setIsCalling] = useState(false);
  const [isAI, setIsAI] = useState<boolean>(false);
  const [listening, setListening] = useState<boolean>(false);
  const [hasAgentStarted, setHasAgentStarted] = useState(false);
  const [isPushToTalkActive, setIsPushToTalkActive] = useState(false);

  // Ref to keep track of microphoneMuted state
  const micMutedRef = useRef(false);

  useEffect(() => {
    // Event listeners to start/stop AI speaking
    retellWebClient.on("agent_start_talking", () => {
      setHasAgentStarted(true);
      setIsAI(true);
      setListening(false);
    });

    retellWebClient.on("agent_stop_talking", () => {
      setIsAI(false);

      // Only automatically start listening if push-to-talk is not active
      if (!micMutedRef.current) {
        setListening(true);
      }
    });

    retellWebClient.on("user_start_talking", () => {
      setIsAI(false);
      setListening(true);
    });

    retellWebClient.on("user_stop_talking", () => {
      setIsAI(true);
      setListening(false);
    });

    // Clean up event listeners on unmount
    return () => {
      retellWebClient.removeAllListeners();
    };
  }, []);

  const toggleConversation = async () => {
    if (isCalling) {
      retellWebClient.stopCall();
      setIsCalling(false);
      setIsAI(false);
      setListening(false);
    } else {
      const registerCallResponse = await registerCall(agentId);
      if (registerCallResponse.access_token) {
        retellWebClient
          .startCall({
            accessToken: registerCallResponse.access_token,
          })
          .catch(console.error);
        setIsCalling(true);
        setIsAI(true);
        setListening(false);

        // Ensure mic is unmuted when starting a call
        micMutedRef.current = false;
        setIsPushToTalkActive(false);
      }
    }
  };

  const toggleMicrophone = () => {
    // Toggle microphone mute state
    const newMutedState = !micMutedRef.current;
    micMutedRef.current = newMutedState;

    // Update UI state
    setIsPushToTalkActive(newMutedState);

    // Mute or unmute the microphone in Retell
    retellWebClient.muteMicrophone(newMutedState);

    // Update listening state
    if (newMutedState) {
      setListening(false);
    } else if (!isAI) {
      // Only set listening to true if AI is not speaking
      setListening(true);
    }
  };

  const handlePushToTalk = (isDown: boolean) => {
    // Only handle push-to-talk if in push-to-talk mode
    if (isCalling && isPushToTalkActive) {
      if (isDown) {
        // Temporarily unmute when key/button is pressed
        retellWebClient.muteMicrophone(false);
        setListening(true);
      } else {
        // Mute again when key/button is released
        retellWebClient.muteMicrophone(true);
        setListening(false);
      }
    }
  };

  // Handle keyboard push-to-talk
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      // Use spacebar as push-to-talk key
      if (e.code === 'Space' && isPushToTalkActive && isCalling) {
        e.preventDefault(); // Prevent scrolling
        handlePushToTalk(true);
      }
    };

    const handleKeyUp = (e: KeyboardEvent) => {
      if (e.code === 'Space' && isPushToTalkActive && isCalling) {
        e.preventDefault();
        handlePushToTalk(false);
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [isPushToTalkActive, isCalling]);

  async function registerCall(agentId: string): Promise<{ access_token: string }> {
    try {
      const response = await fetch("https://al-wathba-backend.onrender.com/create-web-call", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          agent_id: agentId,
        }),
      });

      const data = await response.json();
      return data;
    } catch (err) {
      console.log("Error in registerCall:", err);
      throw new Error(err);
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <h2 style={{ marginBottom: '50px', fontFamily: 'Raleway, sans-serif' }}>Sanjay Demo - Hindi</h2>

        {/* Circle Animation with Audio Spectrum */}
        <div className="pulse-container">
          <div className={`audio-spectrum ${isAI ? 'active' : ''}`}></div>
          <div className={`pulse-animation ${isAI ? 'animate' : ''}`}>
            {isAI && (
              <div className="equalizer">
                <div className="equalizer-bar"></div>
                <div className="equalizer-bar"></div>
                <div className="equalizer-bar"></div>
                <div className="equalizer-bar"></div>
                <div className="equalizer-bar"></div>
              </div>
            )}
            <img
              src="/images/latin-man-avatar-people-04feb2024__9_-ID32942-2000x2000-removebg-preview.png"
              alt="AI"
              className="circle-image"
            />
          </div>
        </div>

        {/* Show "Listening..." text when user speaks */}
        {hasAgentStarted && !isAI && listening && <p className="listening-text">Listening...</p>}

        <div className="controls-container">
          {/* Start/Stop Call Button */}
          <button
            onClick={toggleConversation}
            className={`call-button ${isCalling ? "stop" : "start"}`}
            title={isCalling ? "End Call" : "Start Call"}
          >
            {isCalling ? <TelephoneX size={40} /> : <Telephone size={40} />}
          </button>

          {/* Push-to-Talk Toggle Button - only show when call is active */}
          {isCalling && (
            <button
              onClick={toggleMicrophone}
              className={`mic-button ${isPushToTalkActive ? "muted" : "active"}`}
              title={isPushToTalkActive ? "Push-to-Talk Mode (Space Bar)" : "Always Listening Mode"}
            >
              {isPushToTalkActive ? <MicMute size={40} /> : <Mic size={40} />}
            </button>
          )}
        </div>

        {/* Push-to-Talk Button - only show when push-to-talk is enabled */}
        {isCalling && isPushToTalkActive && (
          <button
            className="push-to-talk-button"
            onMouseDown={() => handlePushToTalk(true)}
            onMouseUp={() => handlePushToTalk(false)}
            onTouchStart={() => handlePushToTalk(true)}
            onTouchEnd={() => handlePushToTalk(false)}
          >
            Hold to Speak
          </button>
        )}

        {/* Push-to-Talk Instructions */}
        {isCalling && isPushToTalkActive && (
          <p className="ptt-instructions">Press and hold the button or Space bar to speak</p>
        )}
      </header>
    </div>
  );
};

export default App;
